import API_CONFIG, { getApiUrl } from '../config/api';

class ApiService {
  static async uploadDocument(file) {
    const formData = new FormData();
    formData.append('file', file);

    const response = await fetch(getApiUrl(API_CONFIG.ENDPOINTS.DOCUMENTS.UPLOAD), {
      method: 'POST',
      body: formData,
    });

    if (!response.ok) {
      const error = await response.json();
      throw new Error(error.detail || 'Failed to process document');
    }

    return response.json();
  }

  static async getCompanyConfig() {
    const response = await fetch(getApiUrl(API_CONFIG.ENDPOINTS.CONFIG.COMPANY));

    if (!response.ok) {
      throw new Error('Failed to load company configuration');
    }

    return response.json();
  }

  static async getComplianceRules() {
    const response = await fetch(getApiUrl(API_CONFIG.ENDPOINTS.CONFIG.COMPLIANCE_RULES));

    if (!response.ok) {
      throw new Error('Failed to load compliance rules');
    }

    return response.json();
  }
}

export default ApiService;
