import React, { useState, useEffect } from 'react';
import { Tab, Popover } from '@headlessui/react';
import {
  DocumentTextIcon,
  ShieldCheckIcon,
  ExclamationTriangleIcon,
  DocumentMagnifyingGlassIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  InformationCircleIcon,
  CheckCircleIcon,
  XCircleIcon,
} from '@heroicons/react/24/outline';

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const StatCard = ({ title, value, color, tooltip, icon: Icon }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className="bg-white overflow-hidden shadow rounded-lg transform transition-all duration-200 hover:scale-105 hover:shadow-lg"
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="px-4 py-5 sm:p-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            {Icon && <Icon className="h-5 w-5 text-gray-400" />}
            <dt className="text-sm font-medium text-gray-500 truncate">{title}</dt>
          </div>
          {tooltip && (
            <Popover className="relative">
              <Popover.Button className="text-gray-400 hover:text-gray-500 focus:outline-none">
                <InformationCircleIcon className="h-5 w-5" />
              </Popover.Button>
              <Popover.Panel className="absolute z-10 w-64 p-2 mt-2 text-sm text-white bg-gray-900 rounded-lg shadow-lg">
                {tooltip}
              </Popover.Panel>
            </Popover>
          )}
        </div>
        <dd className={`mt-1 text-3xl font-semibold ${color}`}>
          {value}
        </dd>
      </div>
    </div>
  );
};

const ComplianceSection = ({ title, items, type = 'missing' }) => {
  const [expandedItems, setExpandedItems] = useState(new Set());

  const toggleItem = (index) => {
    const newExpandedItems = new Set(expandedItems);
    if (newExpandedItems.has(index)) {
      newExpandedItems.delete(index);
    } else {
      newExpandedItems.add(index);
    }
    setExpandedItems(newExpandedItems);
  };

  const getSeverityColor = (severity) => {
    switch (severity.toLowerCase()) {
      case 'high':
        return 'text-red-500';
      case 'medium':
        return 'text-yellow-500';
      case 'low':
        return 'text-green-500';
      default:
        return 'text-gray-500';
    }
  };

  return (
    <div className="mt-4 space-y-4">
      <h3 className="text-lg font-medium text-gray-900">{title}</h3>
      {items.map((item, index) => (
        <div
          key={index}
          className="bg-white shadow sm:rounded-lg overflow-hidden"
        >
          <button
            onClick={() => toggleItem(index)}
            className="w-full px-4 py-5 sm:p-6 flex items-center justify-between hover:bg-gray-50 transition-colors duration-200"
          >
            <div className="flex items-center space-x-3">
              {type === 'missing' ? (
                <XCircleIcon className={`h-5 w-5 ${getSeverityColor(item.severity)}`} />
              ) : (
                <CheckCircleIcon className="h-5 w-5 text-green-500" />
              )}
              <div>
                <h4 className="text-base font-semibold text-gray-900">
                  {item.category.toUpperCase()}
                </h4>
                <p className="text-sm text-gray-500">{item.severity} Priority</p>
              </div>
            </div>
            {expandedItems.has(index) ? (
              <ChevronUpIcon className="h-5 w-5 text-gray-500" />
            ) : (
              <ChevronDownIcon className="h-5 w-5 text-gray-500" />
            )}
          </button>
          <div
            className={`px-4 pb-5 sm:px-6 transition-all duration-200 ${
              expandedItems.has(index) ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
            } overflow-hidden`}
          >
            <p className="mt-2 text-gray-700">{item.clause}</p>
            {item.recommendation && (
              <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                <p className="text-sm text-blue-700">
                  <strong>Recommendation:</strong> {item.recommendation}
                </p>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

const AnalysisResults = ({ results }) => {
  const [companyConfig, setCompanyConfig] = useState(null);

  useEffect(() => {
    // Load company configuration
    fetch('/api/v1/config/company')
      .then(response => response.json())
      .then(data => setCompanyConfig(data))
      .catch(error => console.error('Error loading company config:', error));
  }, []);

  if (!results || !companyConfig) return null;

  const { compliance_report, entities, summary } = results;
  const { risk_levels } = companyConfig;

  const getRiskLevelColor = (level) => {
    const config = risk_levels[level.toLowerCase()];
    return config ? `text-${config.color}-600` : 'text-gray-600';
  };

  const tabs = [
    {
      name: 'Summary',
      icon: DocumentTextIcon,
      content: (
        <div className="prose max-w-none">
          <h3 className="text-lg font-medium text-gray-900">Document Summary</h3>
          <p className="mt-2 text-gray-600">{summary}</p>
        </div>
      ),
    },
    {
      name: 'Compliance',
      icon: ShieldCheckIcon,
      content: (
        <div>
          <h3 className="text-lg font-medium text-gray-900">Compliance Analysis</h3>
          <div className="mt-4 grid grid-cols-1 gap-4 sm:grid-cols-2">
            <StatCard
              title="Risk Level"
              value={compliance_report.summary.risk_level}
              color={getRiskLevelColor(compliance_report.summary.risk_level)}
              tooltip={risk_levels[compliance_report.summary.risk_level.toLowerCase()]?.description}
              icon={ShieldCheckIcon}
            />
            <StatCard
              title="Missing Clauses"
              value={compliance_report.summary.missing_clauses_count}
              color="text-gray-900"
              tooltip="Number of required compliance clauses not found in the document"
              icon={ExclamationTriangleIcon}
            />
            <StatCard
              title="PPE Update Status"
              value={compliance_report.summary.ppe_update_status}
              color="text-gray-900"
              tooltip="Status of Personal Protective Equipment requirements and updates"
              icon={DocumentTextIcon}
            />
            <StatCard
              title="Emergency Drill Status"
              value={compliance_report.summary.drill_status}
              color="text-gray-900"
              tooltip="Status of emergency drill requirements and compliance"
              icon={DocumentMagnifyingGlassIcon}
            />
          </div>

          <div className="mt-8">
            <ComplianceSection
              title="Missing Compliance Clauses"
              items={compliance_report.compliance_analysis.missing_clauses}
              type="missing"
            />
          </div>

          {compliance_report.compliance_analysis.found_clauses && (
            <div className="mt-8">
              <ComplianceSection
                title="Found Compliance Clauses"
                items={compliance_report.compliance_analysis.found_clauses}
                type="found"
              />
            </div>
          )}
        </div>
      ),
    },
    {
      name: 'Entities',
      icon: DocumentMagnifyingGlassIcon,
      content: (
        <div>
          <h3 className="text-lg font-medium text-gray-900">Extracted Entities</h3>
          <div className="mt-4 grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {entities.map((entity, index) => (
              <div
                key={index}
                className="bg-white overflow-hidden shadow rounded-lg transform transition-all duration-200 hover:scale-105 hover:shadow-lg"
              >
                <div className="px-4 py-5 sm:p-6">
                  <dt className="text-sm font-medium text-gray-500 truncate">
                    {entity.label}
                  </dt>
                  <dd className="mt-1 text-sm text-gray-900">
                    {entity.text}
                  </dd>
                </div>
              </div>
            ))}
          </div>
        </div>
      ),
    },
  ];

  return (
    <div className="mt-8">
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Analysis Results</h2>

        <Tab.Group>
          <Tab.List className="flex space-x-1 rounded-xl bg-gray-100 p-1">
            {tabs.map((tab) => (
              <Tab
                key={tab.name}
                className={({ selected }) =>
                  classNames(
                    'w-full rounded-lg py-2.5 text-sm font-medium leading-5',
                    'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2',
                    'transition-all duration-200',
                    selected
