import React, { useState, useEffect } from 'react';
import { Tab, Popover } from '@headlessui/react';
import {
  DocumentTextIcon,
  ShieldCheckIcon,
  ExclamationTriangleIcon,
  DocumentMagnifyingGlassIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  InformationCircleIcon,
  CheckCircleIcon,
  XCircleIcon,
  ChartBarIcon,
  UserGroupIcon,
} from '@heroicons/react/24/outline';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import ApiService from '../services/api';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const StatCard = ({ title, value, color, tooltip, icon }) => (
  <div className="bg-white rounded-lg shadow p-4">
    <div className="flex items-center justify-between">
      <h3 className="text-sm font-medium text-gray-500">{title}</h3>
      <InformationCircleIcon className="h-5 w-5 text-gray-400" title={tooltip} />
    </div>
    <p className={`mt-2 text-2xl font-semibold ${color}`}>{value}</p>
  </div>
);

const ExpandableSection = ({ title, children, defaultExpanded = false }) => {
  const [isExpanded, setIsExpanded] = useState(defaultExpanded);

  return (
    <div className="border rounded-lg overflow-hidden">
      <button
        onClick={() => setIsExpanded(!isExpanded)}
        className="w-full px-4 py-2 bg-gray-50 flex items-center justify-between hover:bg-gray-100"
      >
        <span className="font-medium">{title}</span>
        {isExpanded ? (
          <ChevronUpIcon className="h-5 w-5 text-gray-500" />
        ) : (
          <ChevronDownIcon className="h-5 w-5 text-gray-500" />
        )}
      </button>
      {isExpanded && <div className="p-4">{children}</div>}
    </div>
  );
};

const ComplianceSection = ({ title, items, type = 'missing' }) => {
  const [expandedItems, setExpandedItems] = useState(new Set());

  const toggleItem = (index) => {
    const newExpandedItems = new Set(expandedItems);
    if (newExpandedItems.has(index)) {
      newExpandedItems.delete(index);
    } else {
      newExpandedItems.add(index);
    }
    setExpandedItems(newExpandedItems);
  };

  const getSeverityColor = (severity) => {
    switch (severity.toLowerCase()) {
      case 'high':
        return 'text-red-500';
      case 'medium':
        return 'text-yellow-500';
      case 'low':
        return 'text-green-500';
      default:
        return 'text-gray-500';
    }
  };

  return (
    <div className="mt-4 space-y-4">
      <h3 className="text-lg font-medium text-gray-900">{title}</h3>
      {items.map((item, index) => (
        <div
          key={index}
          className="bg-white shadow sm:rounded-lg overflow-hidden"
        >
          <button
            onClick={() => toggleItem(index)}
            className="w-full px-4 py-5 sm:p-6 flex items-center justify-between hover:bg-gray-50 transition-colors duration-200"
          >
            <div className="flex items-center space-x-3">
              {type === 'missing' ? (
                <XCircleIcon className={`h-5 w-5 ${getSeverityColor(item.severity)}`} />
              ) : (
                <CheckCircleIcon className="h-5 w-5 text-green-500" />
              )}
              <div>
                <h4 className="text-base font-semibold text-gray-900">
                  {item.category.toUpperCase()}
                </h4>
                <p className="text-sm text-gray-500">{item.severity} Priority</p>
              </div>
            </div>
            {expandedItems.has(index) ? (
              <ChevronUpIcon className="h-5 w-5 text-gray-500" />
            ) : (
              <ChevronDownIcon className="h-5 w-5 text-gray-500" />
            )}
          </button>
          <div
            className={`px-4 pb-5 sm:px-6 transition-all duration-200 ${
              expandedItems.has(index) ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
            } overflow-hidden`}
          >
            <p className="mt-2 text-gray-700">{item.clause}</p>
            {item.recommendation && (
              <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                <p className="text-sm text-blue-700">
                  <strong>Recommendation:</strong> {item.recommendation}
                </p>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

const ClauseTraceabilitySection = ({ traceResults }) => {
  if (!traceResults) return null;

  return (
    <div className="mt-6">
      <h3 className="text-lg font-medium text-gray-900 mb-4">Clause Traceability</h3>
      <div className="space-y-4">
        {Object.entries(traceResults).map(([clauseName, data]) => (
          <div key={clauseName} className="bg-white shadow rounded-lg p-4">
            <div className="flex items-center justify-between">
              <h4 className="text-md font-medium text-gray-900">{clauseName}</h4>
              <span className={`px-2 py-1 text-sm rounded-full ${
                data.status === 'Found' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
              }`}>
                {data.status}
              </span>
            </div>
            {data.matches && data.matches.length > 0 && (
              <div className="mt-2">
                <p className="text-sm text-gray-500">Found matches:</p>
                <ul className="mt-1 space-y-2">
                  {data.matches.map((match, index) => (
                    <li key={index} className="text-sm text-gray-700 bg-gray-50 p-2 rounded">
                      <p className="font-medium">Location: {match.location}</p>
                      <p className="mt-1">{match.context}</p>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

const AnalysisResults = ({ results }) => {
  const [companyConfig, setCompanyConfig] = useState(null);

  useEffect(() => {
    console.log('AnalysisResults mounted with results:', results);
    ApiService.getCompanyConfig()
      .then(data => {
        console.log('Company config loaded:', data);
        setCompanyConfig(data);
      })
      .catch(error => {
        console.error('Error loading company config:', error);
        setCompanyConfig({
          risk_levels: {
            high: { color: '#ef4444' },
            medium: { color: '#f59e0b' },
            low: { color: '#10b981' }
          }
        });
      });
  }, []);

  if (!results) {
    console.log('No results available');
    return null;
  }

  if (!companyConfig) {
    console.log('Loading company config...');
    return (
      <div className="mt-8 text-center">
        <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        <p className="mt-2 text-gray-600">Loading analysis results...</p>
      </div>
    );
  }

  const { compliance_report, entities, summary } = results;
  const { risk_levels } = companyConfig;

  // Debug logs
  console.log('Compliance Report:', compliance_report);
  console.log('Entities:', entities);

  // Prepare data for charts
  const entityTypeData = {
    labels: [...new Set(entities.map(e => e.label))],
    datasets: [{
      label: 'Entity Types',
      data: [...new Set(entities.map(e => e.label))].map(label => 
        entities.filter(e => e.label === label).length
      ),
      backgroundColor: [
        'rgba(255, 99, 132, 0.6)',
        'rgba(54, 162, 235, 0.6)',
        'rgba(255, 206, 86, 0.6)',
        'rgba(75, 192, 192, 0.6)',
        'rgba(153, 102, 255, 0.6)',
      ],
      borderColor: [
        'rgba(255, 99, 132, 1)',
        'rgba(54, 162, 235, 1)',
        'rgba(255, 206, 86, 1)',
        'rgba(75, 192, 192, 1)',
        'rgba(153, 102, 255, 1)',
      ],
      borderWidth: 1,
    }]
  };

  const complianceData = {
    labels: ['Missing Clauses', 'Compliant Clauses'],
    datasets: [{
      data: [
        compliance_report.missing_clauses_count,
        (compliance_report.total_clauses || 10) - compliance_report.missing_clauses_count
      ],
      backgroundColor: [
        'rgba(239, 68, 68, 0.6)',
        'rgba(16, 185, 129, 0.6)',
      ],
      borderColor: [
        'rgba(239, 68, 68, 1)',
        'rgba(16, 185, 129, 1)',
      ],
      borderWidth: 1,
    }]
  };

  const getRiskLevelColor = (level) => {
    const config = risk_levels[level.toLowerCase()];
    if (!config) return 'text-gray-600';
    return `text-[${config.color}]`;
  };

  const tabs = [
    {
      name: 'Dashboard',
      icon: ChartBarIcon,
      content: (
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <StatCard
              title="Risk Level"
              value={compliance_report.risk_level}
              color={getRiskLevelColor(compliance_report.risk_level)}
              tooltip="Overall risk level based on compliance analysis"
              icon={ExclamationTriangleIcon}
            />
            <StatCard
              title="Missing Clauses"
              value={compliance_report.missing_clauses_count}
              color="text-gray-900"
              tooltip="Number of required clauses not found in the document"
              icon={XCircleIcon}
            />
            <StatCard
              title="PPE Status"
              value={compliance_report.ppe_update_status}
              color={compliance_report.ppe_update_status === "Up to date" ? "text-green-600" : "text-yellow-600"}
              tooltip="Status of Personal Protective Equipment requirements"
              icon={ShieldCheckIcon}
            />
            <StatCard
              title="Emergency Drill"
              value={compliance_report.emergency_drill_status}
              color={compliance_report.emergency_drill_status === "Compliant" ? "text-green-600" : "text-red-600"}
              tooltip="Status of emergency drill requirements"
              icon={CheckCircleIcon}
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Compliance Overview</h3>
              <div className="h-64">
                <Doughnut
                  data={complianceData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        position: 'bottom',
                        labels: {
                          padding: 20,
                          font: {
                            size: 12
                          }
                        }
                      },
                      tooltip: {
                        enabled: true,
                        mode: 'index',
                        intersect: false,
                      }
                    },
                    animation: {
                      duration: 1000,
                      easing: 'easeInOutQuart'
                    }
                  }}
                />
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Entity Distribution</h3>
              <div className="h-64">
                <Bar
                  data={entityTypeData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        display: false,
                      },
                      tooltip: {
                        enabled: true,
                        mode: 'index',
                        intersect: false,
                      }
                    },
                    scales: {
                      y: {
                        beginAtZero: true,
                        ticks: {
                          stepSize: 1,
                        },
                      },
                    },
                    animation: {
                      duration: 1000,
                      easing: 'easeInOutQuart'
                    }
                  }}
                />
              </div>
            </div>
          </div>

          {compliance_report.missing_clauses && compliance_report.missing_clauses.length > 0 && (
            <ExpandableSection title="Missing Clauses" defaultExpanded={true}>
              <div className="space-y-4">
                {compliance_report.missing_clauses.map((clause, index) => (
                  <div key={index} className="bg-red-50 p-4 rounded-lg hover:bg-red-100 transition-colors duration-200">
                    <p className="text-red-800 font-medium">{clause.text}</p>
                    <p className="text-red-600 text-sm mt-1">Status: {clause.status}</p>
                  </div>
                ))}
              </div>
            </ExpandableSection>
          )}
        </div>
      ),
    },
    {
      name: 'Summary',
      icon: DocumentTextIcon,
      content: (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg p-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Document Summary</h3>
            <p className="text-gray-700">{summary}</p>
          </div>
          <ClauseTraceabilitySection traceResults={compliance_report?.clause_traceability} />
        </div>
      ),
    },
    {
      name: 'Entities',
      icon: DocumentMagnifyingGlassIcon,
      content: (
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Extracted Entities</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {entities.map((entity, index) => (
              <div key={index} className="bg-gray-50 p-4 rounded-lg hover:bg-gray-100 transition-colors duration-200">
                <p className="text-sm font-medium text-gray-500">{entity.label}</p>
                <p className="mt-1 text-lg font-semibold text-gray-900">{entity.text}</p>
              </div>
            ))}
          </div>
        </div>
      ),
    },
  ];

  return (
    <div className="mt-8">
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Analysis Results</h2>

        <Tab.Group>
          <Tab.List className="flex space-x-1 rounded-xl bg-gray-100 p-1">
            {tabs.map((tab) => (
              <Tab
                key={tab.name}
                className={({ selected }) =>
                  classNames(
                    'w-full rounded-lg py-2.5 text-sm font-medium leading-5',
                    'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2',
                    'transition-all duration-200',
                    selected
                      ? 'bg-white shadow text-blue-700 scale-105'
                      : 'text-gray-600 hover:bg-white/[0.12] hover:text-gray-800'
                  )
                }
              >
                <div className="flex items-center justify-center space-x-2">
                  <tab.icon className="h-5 w-5" />
                  <span>{tab.name}</span>
                </div>
              </Tab>
            ))}
          </Tab.List>
          <Tab.Panels className="mt-6">
            {tabs.map((tab, idx) => (
              <Tab.Panel
                key={idx}
                className={classNames(
                  'rounded-xl bg-white p-6',
                  'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2',
                  'transition-all duration-200'
                )}
              >
                {tab.content}
              </Tab.Panel>
            ))}
          </Tab.Panels>
        </Tab.Group>
      </div>
    </div>
  );
};

export default AnalysisResults;
