import React, { useState, useEffect } from 'react';
import { Tab } from '@headlessui/react';
import {
  DocumentTextIcon,
  ShieldCheckIcon,
  ExclamationTriangleIcon,
  DocumentMagnifyingGlassIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  InformationCircleIcon,
  CheckCircleIcon,
  XCircleIcon,
  ChartBarIcon,
} from '@heroicons/react/24/outline';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import apiService from '../services/api';
import jsPDF from 'jspdf';
import 'jspdf-autotable';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const StatCard = ({ title, value, color, tooltip, icon }) => (
  <div className="bg-white rounded-lg shadow p-4">
    <div className="flex items-center justify-between">
      <h3 className="text-sm font-medium text-gray-500">{title}</h3>
      <InformationCircleIcon className="h-5 w-5 text-gray-400" title={tooltip} />
    </div>
    <p className={`mt-2 text-2xl font-semibold ${color}`}>{value}</p>
  </div>
);

const ExpandableSection = ({ title, children, defaultExpanded = false }) => {
  const [isExpanded, setIsExpanded] = useState(defaultExpanded);

  return (
    <div className="border rounded-lg overflow-hidden">
      <button
        onClick={() => setIsExpanded(!isExpanded)}
        className="w-full px-4 py-2 bg-gray-50 flex items-center justify-between hover:bg-gray-100"
      >
        <span className="font-medium">{title}</span>
        {isExpanded ? (
          <ChevronUpIcon className="h-5 w-5 text-gray-500" />
        ) : (
          <ChevronDownIcon className="h-5 w-5 text-gray-500" />
        )}
      </button>
      {isExpanded && <div className="p-4">{children}</div>}
    </div>
  );
};

const ComplianceSection = ({ title, items, type = 'missing' }) => {
  const [expandedItems, setExpandedItems] = useState(new Set());

  const toggleItem = (index) => {
    const newExpandedItems = new Set(expandedItems);
    if (newExpandedItems.has(index)) {
      newExpandedItems.delete(index);
    } else {
      newExpandedItems.add(index);
    }
    setExpandedItems(newExpandedItems);
  };

  const getSeverityColor = (severity) => {
    switch (severity?.toLowerCase()) {
      case 'high':
        return 'text-red-500';
      case 'medium':
        return 'text-yellow-500';
      case 'low':
        return 'text-green-500';
      default:
        return 'text-gray-500';
    }
  };

  if (!items || items.length === 0) {
    return (
      <div className="mt-4">
        <h3 className="text-lg font-medium text-gray-900">{title}</h3>
        <p className="mt-2 text-gray-500">No items to display.</p>
      </div>
    );
  }

  return (
    <div className="mt-4 space-y-4">
      <h3 className="text-lg font-medium text-gray-900">{title}</h3>
      {items.map((item, index) => (
        <div
          key={index}
          className="bg-white shadow sm:rounded-lg overflow-hidden"
        >
          <button
            onClick={() => toggleItem(index)}
            className="w-full px-4 py-5 sm:p-6 flex items-center justify-between hover:bg-gray-50 transition-colors duration-200"
          >
            <div className="flex items-center space-x-3">
              {type === 'missing' ? (
                <XCircleIcon className={`h-5 w-5 ${getSeverityColor(item.severity)}`} />
              ) : (
                <CheckCircleIcon className="h-5 w-5 text-green-500" />
              )}
              <div>
                <h4 className="text-base font-semibold text-gray-900">
                  {item.category?.toUpperCase() || 'UNCATEGORIZED'}
                </h4>
                <p className="text-sm text-gray-500">{item.severity || 'Unknown'} Priority</p>
              </div>
            </div>
            {expandedItems.has(index) ? (
              <ChevronUpIcon className="h-5 w-5 text-gray-500" />
            ) : (
              <ChevronDownIcon className="h-5 w-5 text-gray-500" />
            )}
          </button>
          <div
            className={`px-4 pb-5 sm:px-6 transition-all duration-200 ${
              expandedItems.has(index) ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
            } overflow-hidden`}
          >
            <p className="mt-2 text-gray-700">{item.clause}</p>
            {item.recommendation && (
              <div className="mt-4 p-4 bg-blue-50 rounded-lg">
                <p className="text-sm text-blue-700">
                  <strong>Recommendation:</strong> {item.recommendation}
                </p>
              </div>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

const ClauseTraceabilitySection = ({ traceResults }) => {
  if (!traceResults || Object.keys(traceResults).length === 0) {
    return (
      <div className="mt-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center gap-2">
          Clause Traceability
          <span className="relative group">
            <InformationCircleIcon className="h-5 w-5 text-blue-400 cursor-pointer" />
            <span className="absolute left-8 top-1/2 -translate-y-1/2 z-10 hidden group-hover:block bg-gray-900 text-white text-xs rounded px-2 py-1 whitespace-nowrap shadow-lg">
              Shows which required clauses were found or missing in your document, based on your selected compliance standard.
            </span>
          </span>
        </h3>
        <p className="text-gray-500">No clause traceability results available.</p>
      </div>
    );
  }

  return (
    <div className="mt-6">
      <h3 className="text-lg font-medium text-gray-900 mb-4 flex items-center gap-2">
        Clause Traceability
        <span className="relative group">
          <InformationCircleIcon className="h-5 w-5 text-blue-400 cursor-pointer" />
          <span className="absolute left-8 top-1/2 -translate-y-1/2 z-10 hidden group-hover:block bg-gray-900 text-white text-xs rounded px-2 py-1 whitespace-nowrap shadow-lg">
            Shows which required clauses were found or missing in your document, based on your selected compliance standard.
          </span>
        </span>
      </h3>
      <div className="space-y-4">
        {Object.values(traceResults).map((clause, idx) => (
          <div key={clause.id || idx} className="bg-white shadow rounded-lg p-4">
            <div className="flex items-center justify-between mb-2">
              <div className="flex flex-col">
                <span className="text-xs text-gray-400">ID: {clause.id}</span>
                <h4 className="text-md font-medium text-gray-900 flex items-center gap-2">
                  {clause.title}
                  <span className={`ml-2 px-2 py-1 text-xs rounded-full ${
                    clause.status === 'Found' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                  }`}>
                    {clause.status}
                  </span>
                  <span className={`ml-2 px-2 py-1 text-xs rounded-full ${
                    clause.severity === 'high' ? 'bg-red-100 text-red-800' : clause.severity === 'medium' ? 'bg-yellow-100 text-yellow-800' : 'bg-green-100 text-green-800'
                  }`}>
                    {clause.severity?.toUpperCase()}
                  </span>
                  <span className="relative group ml-2">
                    <InformationCircleIcon className="h-4 w-4 text-blue-400 cursor-pointer" />
                    <span className="absolute left-6 top-1/2 -translate-y-1/2 z-10 hidden group-hover:block bg-gray-900 text-white text-xs rounded px-2 py-1 whitespace-nowrap shadow-lg">
                      This clause is matched using fuzzy keyword search. Paragraphs with a similarity score above 80 are considered matches.
                    </span>
                  </span>
                </h4>
              </div>
            </div>
            {clause.recommendation && (
              <div className="mb-2 p-2 bg-blue-50 rounded">
                <span className="text-xs text-blue-700">Recommendation: {clause.recommendation}</span>
              </div>
            )}
            {clause.matched_paragraphs && clause.matched_paragraphs.length > 0 ? (
              <div className="mt-2">
                <p className="text-sm text-gray-500 mb-1">Matched Paragraphs:</p>
                <ul className="space-y-2">
                  {clause.matched_paragraphs.map((match, i) => (
                    <li key={i} className="text-sm text-gray-700 bg-gray-50 p-2 rounded">
                      <span className="font-medium">Paragraph {match.paragraph_number}:</span> <span className="text-xs text-gray-400">[Keyword: {match.keyword}]</span>
                      <div className="mt-1">{match.snippet}</div>
                    </li>
                  ))}
                </ul>
              </div>
            ) : (
              <div className="mt-2 text-sm text-red-500">No matches found for this clause.</div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

const AnalysisResults = ({ results }) => {
  const [companyConfig, setCompanyConfig] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [severityFilter, setSeverityFilter] = useState('All');

  useEffect(() => {
    console.log('AnalysisResults mounted with results:', results);
    setIsLoading(true);

    apiService.getCompanyConfig()
      .then(data => {
        console.log('Company config loaded:', data);
        setCompanyConfig(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error loading company config:', error);
        setCompanyConfig({
          risk_levels: {
            high: { color: '#ef4444' },
            medium: { color: '#f59e0b' },
            low: { color: '#10b981' }
          }
        });
        setIsLoading(false);
      });
  }, [results]);

  if (!results) {
    console.log('No results available');
    return (
      <div className="mt-8 text-center">
        <p className="text-gray-600">No analysis results available. Please upload a document to begin.</p>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="mt-8 text-center">
        <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
        <p className="mt-2 text-gray-600">Loading analysis results...</p>
      </div>
    );
  }

  const { compliance_report, entities, summary, clause_traceability } = results;
  const { risk_levels } = companyConfig;

  // Debug logs
  console.log('Compliance Report:', compliance_report);
  console.log('Entities:', entities);
  console.log('Summary:', summary);
  console.log('Clause Traceability:', clause_traceability);

  // Prepare data for charts
  const entityTypeData = {
    labels: [...new Set(entities?.map(e => e.label) || [])],
    datasets: [{
      label: 'Entity Types',
      data: [...new Set(entities?.map(e => e.label) || [])].map(label => 
        entities.filter(e => e.label === label).length
      ),
      backgroundColor: 'rgba(59, 130, 246, 0.5)',
      borderColor: 'rgb(59, 130, 246)',
      borderWidth: 1
    }]
  };

  const complianceData = {
    labels: ['Missing Clauses', 'Compliant Clauses'],
    datasets: [{
      data: [
        compliance_report?.missing_requirements?.length || 0,
        compliance_report?.compliant_requirements?.length || 0
      ],
      backgroundColor: [
        'rgba(239, 68, 68, 0.6)',
        'rgba(16, 185, 129, 0.6)',
      ],
      borderColor: [
        'rgba(239, 68, 68, 1)',
        'rgba(16, 185, 129, 1)',
      ],
      borderWidth: 1
    }]
  };

  const getRiskLevelColor = (level) => {
    const config = risk_levels[level?.toLowerCase()];
    if (!config) return 'text-gray-600';
    return `text-[${config.color}]`;
  };

  // Filtered requirements by severity
  const filterBySeverity = (items) => {
    if (severityFilter === 'All') return items;
    return items.filter(item => item.severity && item.severity.toLowerCase() === severityFilter.toLowerCase());
  };

  // Export to PDF handler
  const handleExportPDF = () => {
    const doc = new jsPDF();
    doc.setFontSize(16);
    doc.text('Compliance Analysis Report', 14, 16);
    doc.setFontSize(12);
    doc.text(`Risk Level: ${compliance_report?.risk_level || 'Unknown'}`, 14, 28);
    doc.text('Summary:', 14, 38);
    doc.setFontSize(10);
    doc.text(summary || '', 14, 44, { maxWidth: 180 });
    doc.setFontSize(12);
    // Missing Clauses Table
    doc.text('Missing Clauses:', 14, 60);
    doc.autoTable({
      startY: 64,
      head: [['ID', 'Title', 'Severity', 'Recommendation']],
      body: (compliance_report?.missing_requirements || []).map(item => [
        item.id || '',
        item.title || '',
        item.severity || '',
        item.recommendation || ''
      ]),
      theme: 'striped',
      headStyles: { fillColor: [239, 68, 68] },
      styles: { fontSize: 9, cellWidth: 'wrap' },
      columnStyles: { 0: { cellWidth: 20 }, 1: { cellWidth: 60 }, 2: { cellWidth: 20 }, 3: { cellWidth: 70 } }
    });
    // Compliant Clauses Table
    let y = doc.lastAutoTable ? doc.lastAutoTable.finalY + 10 : 80;
    doc.text('Compliant Clauses:', 14, y);
    doc.autoTable({
      startY: y + 4,
      head: [['ID', 'Title', 'Severity', 'Recommendation']],
      body: (compliance_report?.compliant_requirements || []).map(item => [
        item.id || '',
        item.title || '',
        item.severity || '',
        item.recommendation || ''
      ]),
      theme: 'striped',
      headStyles: { fillColor: [16, 185, 129] },
      styles: { fontSize: 9, cellWidth: 'wrap' },
      columnStyles: { 0: { cellWidth: 20 }, 1: { cellWidth: 60 }, 2: { cellWidth: 20 }, 3: { cellWidth: 70 } }
    });
    // Clause Traceability Table
    y = doc.lastAutoTable ? doc.lastAutoTable.finalY + 10 : y + 40;
    doc.text('Clause Traceability:', 14, y);
    doc.autoTable({
      startY: y + 4,
      head: [['ID', 'Title', 'Severity', 'Status', 'Matched Paragraphs']],
      body: Object.values(clause_traceability || {}).map(clause => [
        clause.id || '',
        clause.title || '',
        clause.severity || '',
        clause.status || '',
        (clause.matched_paragraphs || []).map(mp => `#${mp.paragraph_number}: ${mp.snippet}`).join('\n')
      ]),
      theme: 'striped',
      headStyles: { fillColor: [59, 130, 246] },
      styles: { fontSize: 8, cellWidth: 'wrap' },
      columnStyles: { 0: { cellWidth: 20 }, 1: { cellWidth: 50 }, 2: { cellWidth: 20 }, 3: { cellWidth: 20 }, 4: { cellWidth: 80 } }
    });
    doc.save('compliance_analysis_report.pdf');
  };

  const tabs = [
    {
      name: 'Dashboard',
      icon: ChartBarIcon,
      content: (
        <div className="space-y-6">
          {/* Export to PDF Button */}
          <div className="flex justify-end mb-2">
            <button
              onClick={handleExportPDF}
              className="px-4 py-2 bg-blue-600 text-white rounded shadow hover:bg-blue-700 transition-colors"
            >
              Export to PDF
            </button>
          </div>
          {/* Severity Filter UI */}
          <div className="flex items-center gap-2 mb-4">
            <span className="font-medium text-gray-700">Filter by Severity:</span>
            {['All', 'High', 'Medium', 'Low'].map(level => (
              <button
                key={level}
                className={`px-3 py-1 rounded-full border text-xs font-semibold transition-colors duration-150 ${
                  severityFilter === level
                    ? level === 'High'
                      ? 'bg-red-500 text-white border-red-500'
                      : level === 'Medium'
                      ? 'bg-yellow-400 text-white border-yellow-400'
                      : level === 'Low'
                      ? 'bg-green-500 text-white border-green-500'
                      : 'bg-gray-700 text-white border-gray-700'
                    : 'bg-white text-gray-700 border-gray-300 hover:bg-gray-100'
                }`}
                onClick={() => setSeverityFilter(level)}
              >
                {level}
              </button>
            ))}
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <StatCard
              title="Risk Level"
              value={compliance_report?.risk_level || 'Unknown'}
              color={getRiskLevelColor(compliance_report?.risk_level)}
              tooltip="Overall risk level based on compliance analysis"
              icon={ExclamationTriangleIcon}
            />
            <StatCard
              title="Missing Clauses"
              value={filterBySeverity(compliance_report?.missing_requirements || []).length}
              color="text-gray-900"
              tooltip="Number of required clauses not found in the document"
              icon={XCircleIcon}
            />
            <StatCard
              title="Compliant Clauses"
              value={filterBySeverity(compliance_report?.compliant_requirements || []).length}
              color="text-green-600"
              tooltip="Number of compliant clauses found in the document"
              icon={CheckCircleIcon}
            />
            <StatCard
              title="Total Entities"
              value={entities?.length || 0}
              color="text-blue-600"
              tooltip="Total number of entities extracted from the document"
              icon={DocumentMagnifyingGlassIcon}
            />
          </div>

          <div className="space-y-6">
            <ComplianceSection
              title="Missing Requirements"
              items={filterBySeverity(compliance_report?.missing_requirements || [])}
              type="missing"
            />
            <ComplianceSection
              title="Compliant Requirements"
              items={filterBySeverity(compliance_report?.compliant_requirements || [])}
              type="compliant"
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Compliance Overview</h3>
              <div className="h-64">
                <Doughnut
                  data={complianceData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        position: 'bottom',
                        labels: {
                          padding: 20,
                          font: {
                            size: 12
                          }
                        }
                      }
                    }
                  }}
                />
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Entity Distribution</h3>
              <div className="h-64">
                <Bar
                  data={entityTypeData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        display: false
                      }
                    },
                    scales: {
                      y: {
                        beginAtZero: true,
                        ticks: {
                          stepSize: 1
                        }
                      }
                    }
                  }}
                />
              </div>
            </div>
          </div>
        </div>
      )
    },
    {
      name: 'Summary',
      icon: DocumentTextIcon,
      content: (
        <div className="space-y-6">
          <div className="bg-white shadow rounded-lg p-6">
            <h3 className="text-lg font-medium text-gray-900 mb-4">Document Summary</h3>
            <p className="text-gray-700 whitespace-pre-wrap">{summary}</p>
          </div>
          <ClauseTraceabilitySection traceResults={clause_traceability} />
        </div>
      )
    },
    {
      name: 'Entities',
      icon: DocumentMagnifyingGlassIcon,
      content: (
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Extracted Entities</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {entities?.map((entity, index) => (
              <div key={index} className="bg-gray-50 p-4 rounded-lg hover:bg-gray-100 transition-colors duration-200">
                <p className="text-sm font-medium text-gray-500">{entity.label}</p>
                <p className="mt-1 text-lg font-semibold text-gray-900">{entity.text}</p>
              </div>
            ))}
          </div>
        </div>
      )
    }
  ];

  return (
    <div className="mt-8">
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Analysis Results</h2>

        <Tab.Group>
          <Tab.List className="flex space-x-1 rounded-xl bg-gray-100 p-1">
            {tabs.map((tab) => (
              <Tab
                key={tab.name}
                className={({ selected }) =>
                  classNames(
                    'w-full rounded-lg py-2.5 text-sm font-medium leading-5',
                    'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2',
                    'transition-all duration-200',
                    selected
                      ? 'bg-white shadow text-blue-700 scale-105'
                      : 'text-gray-600 hover:bg-white/[0.12] hover:text-gray-800'
                  )
                }
              >
                <div className="flex items-center justify-center space-x-2">
                  <tab.icon className="h-5 w-5" />
                  <span>{tab.name}</span>
                </div>
              </Tab>
            ))}
          </Tab.List>
          <Tab.Panels className="mt-6">
            {tabs.map((tab, idx) => (
              <Tab.Panel
                key={idx}
                className={classNames(
                  'rounded-xl bg-white p-6',
                  'ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2',
                  'transition-all duration-200'
                )}
              >
                {tab.content}
              </Tab.Panel>
            ))}
          </Tab.Panels>
        </Tab.Group>
      </div>
    </div>
  );
};

export default AnalysisResults;
