import React, { useState, useEffect } from 'react';
import { Tab } from '@headlessui/react';
import {
  DocumentTextIcon,
  ExclamationTriangleIcon,
  DocumentMagnifyingGlassIcon,
  ChevronDownIcon,
  ChevronUpIcon,
  InformationCircleIcon,
  CheckCircleIcon,
  XCircleIcon,
  ChartBarIcon,
  UserGroupIcon,
  ArrowDownTrayIcon
} from '@heroicons/react/24/outline';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement,
} from 'chart.js';
import { Bar, Doughnut } from 'react-chartjs-2';
import apiService from '../services/api';
import { jsPDF } from 'jspdf';
import autoTable from 'jspdf-autotable';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
  PointElement,
  LineElement
);

function classNames(...classes) {
  return classes.filter(Boolean).join(' ');
}

const StatCard = ({ title, value, color, tooltip, icon: Icon, trend }) => (
  <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6 hover:shadow-md transition-all duration-200 transform hover:scale-105">
    <div className="flex items-center justify-between mb-4">
      <div className={`p-3 rounded-lg ${color} bg-opacity-10`}>
        <Icon className={`h-6 w-6 ${color.replace('bg-', 'text-')}`} />
      </div>
      <InformationCircleIcon className="h-5 w-5 text-gray-400" title={tooltip} />
    </div>
    <h3 className="text-sm font-medium text-gray-600 mb-1">{title}</h3>
    <div className="flex items-baseline space-x-2">
      <p className={`text-3xl font-bold ${color.replace('bg-', 'text-')}`}>{value}</p>
      {trend && (
        <span className={`text-sm font-medium ${trend > 0 ? 'text-green-600' : 'text-red-600'}`}>
          {trend > 0 ? '+' : ''}{trend}%
        </span>
      )}
    </div>
  </div>
);

const ClauseTraceabilitySection = ({ traceResults }) => {
  const [expandedClauses, setExpandedClauses] = useState(new Set());

  const toggleClause = (clauseId) => {
    const newExpanded = new Set(expandedClauses);
    if (newExpanded.has(clauseId)) {
      newExpanded.delete(clauseId);
    } else {
      newExpanded.add(clauseId);
    }
    setExpandedClauses(newExpanded);
  };

  if (!traceResults || Object.keys(traceResults).length === 0) {
    return (
      <div className="mt-8">
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-8 text-center">
          <DocumentMagnifyingGlassIcon className="mx-auto h-12 w-12 text-gray-400 mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">Clause Traceability</h3>
          <p className="text-gray-500">No clause traceability results available for this document.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="mt-8">
      <div className="flex items-center justify-between mb-6">
        <h3 className="text-xl font-semibold text-gray-900 flex items-center gap-2">
          <DocumentMagnifyingGlassIcon className="h-6 w-6 text-blue-500" />
          Clause Traceability
        </h3>
        <span className="text-sm text-gray-500">
          {Object.keys(traceResults).length} clauses analyzed
        </span>
      </div>
      <div className="space-y-4">
        {Object.values(traceResults).map((clause, idx) => {
          // Ensure we have a valid string ID for the key and toggle function
          const clauseId = typeof clause.id === 'string' ? clause.id : `clause-${idx}`;
          const isExpanded = expandedClauses.has(clauseId);

          return (
            <div key={clauseId} className="bg-white rounded-xl shadow-sm border border-gray-200 overflow-hidden hover:shadow-md transition-shadow">
              <div 
                className="p-6 cursor-pointer"
                onClick={() => toggleClause(clauseId)}
              >
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <span className="text-xs font-medium text-gray-400 bg-gray-100 px-2 py-1 rounded">
                        {typeof clause.id === 'string' ? clause.id : `Clause ${idx + 1}`}
                      </span>
                      <span className={`px-3 py-1 text-xs font-medium rounded-full ${
                        clause.status === 'Found' 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-red-100 text-red-800'
                      }`}>
                        {clause.status || 'Unknown'}
                      </span>
                      <span className={`px-3 py-1 text-xs font-medium rounded-full ${
                        clause.severity === 'high' 
                          ? 'bg-red-100 text-red-800' 
                          : clause.severity === 'medium' 
                          ? 'bg-yellow-100 text-yellow-800' 
                          : 'bg-green-100 text-green-800'
                      }`}>
                        {(clause.severity || 'low').toUpperCase()}
                      </span>
                    </div>
                    <h4 className="text-lg font-semibold text-gray-900 mb-2">
                      {clause.title || `Clause ${idx + 1}`}
                    </h4>
                    {clause.recommendation && (
                      <div className="bg-blue-50 border border-blue-200 rounded-lg p-3 mb-3">
                        <p className="text-sm text-blue-800">
                          <strong>Recommendation:</strong> {clause.recommendation}
                        </p>
                      </div>
                    )}
                  </div>
                  <div className="flex items-center gap-2">
                    {isExpanded ? (
                      <ChevronUpIcon className="h-5 w-5 text-gray-400" />
                    ) : (
                      <ChevronDownIcon className="h-5 w-5 text-gray-400" />
                    )}
                  </div>
                </div>
              </div>

              {isExpanded && (
                <div className="border-t border-gray-200 bg-gray-50 p-6">
                  {clause.matched_paragraphs && clause.matched_paragraphs.length > 0 ? (
                    <div>
                      <h5 className="text-sm font-medium text-gray-900 mb-3 flex items-center gap-2">
                        <CheckCircleIcon className="h-4 w-4 text-green-500" />
                        Matched Paragraphs ({clause.matched_paragraphs.length})
                      </h5>
                      <div className="space-y-3">
                        {clause.matched_paragraphs.map((match, i) => (
                          <div key={i} className="bg-white rounded-lg p-4 border border-gray-200">
                            <div className="flex items-center justify-between mb-2">
                              <span className="text-sm font-medium text-gray-700">
                                Paragraph {match.paragraph_number || i + 1}
                              </span>
                              <span className="text-xs text-gray-500 bg-gray-100 px-2 py-1 rounded">
                                Keyword: {match.keyword || 'N/A'}
                              </span>
                            </div>
                            <p className="text-sm text-gray-700 leading-relaxed">
                              {match.snippet || 'No snippet available'}
                            </p>
                          </div>
                        ))}
                      </div>
                    </div>
                  ) : (
                    <div className="text-center py-4">
                      <XCircleIcon className="mx-auto h-8 w-8 text-red-400 mb-2" />
                      <p className="text-sm text-red-600 font-medium">No matches found for this clause</p>
                      <p className="text-xs text-gray-500 mt-1">Consider adding relevant content to address this requirement</p>
                    </div>
                  )}
                </div>
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
};

const AnalysisResults = ({ results }) => {
  const [companyConfig, setCompanyConfig] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [severityFilter, setSeverityFilter] = useState('All');
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    console.log('AnalysisResults mounted with results:', results);
    setIsLoading(true);

    apiService.getCompanyConfig()
      .then(data => {
        console.log('Company config loaded:', data);
        setCompanyConfig(data);
        setIsLoading(false);
      })
      .catch(error => {
        console.error('Error loading company config:', error);
        setCompanyConfig({
          risk_levels: {
            high: { color: '#ef4444' },
            medium: { color: '#f59e0b' },
            low: { color: '#10b981' }
          }
        });
        setIsLoading(false);
      });
  }, [results]);

  if (!results) {
    console.log('No results available');
    return (
      <div className="mt-8 text-center">
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-8">
          <DocumentTextIcon className="mx-auto h-12 w-12 text-gray-400 mb-4" />
          <p className="text-gray-600">No analysis results available. Please upload a document to begin.</p>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="mt-8 text-center">
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-8">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500 mb-4"></div>
          <p className="text-gray-600">Loading analysis results...</p>
        </div>
      </div>
    );
  }

  const { compliance_report, entities, summary, clause_traceability } = results;

  // Debug logs
  console.log('Compliance Report:', compliance_report);
  console.log('Entities:', entities);
  console.log('Summary:', summary);
  console.log('Clause Traceability:', clause_traceability);

  // Prepare data for charts
  const entityTypeData = {
    labels: [...new Set(entities?.map(e => e.label) || [])],
    datasets: [{
      label: 'Entity Count',
      data: [...new Set(entities?.map(e => e.label) || [])].map(label => 
        entities?.filter(e => e.label === label).length || 0
      ),
      backgroundColor: [
        'rgba(59, 130, 246, 0.8)',
        'rgba(16, 185, 129, 0.8)',
        'rgba(245, 158, 11, 0.8)',
        'rgba(239, 68, 68, 0.8)',
        'rgba(139, 92, 246, 0.8)',
      ],
      borderColor: [
        'rgba(59, 130, 246, 1)',
        'rgba(16, 185, 129, 1)',
        'rgba(245, 158, 11, 1)',
        'rgba(239, 68, 68, 1)',
        'rgba(139, 92, 246, 1)',
      ],
      borderWidth: 2,
    }]
  };

  const complianceData = {
    labels: ['Compliant', 'Non-Compliant', 'Pending Review'],
    datasets: [{
      data: [
        compliance_report?.compliant_requirements?.length || 0,
        compliance_report?.missing_requirements?.length || 0,
        compliance_report?.pending_requirements?.length || 0
      ],
      backgroundColor: [
        'rgba(16, 185, 129, 0.8)',
        'rgba(239, 68, 68, 0.8)',
        'rgba(245, 158, 11, 0.8)',
      ],
      borderColor: [
        'rgba(16, 185, 129, 1)',
        'rgba(239, 68, 68, 1)',
        'rgba(245, 158, 11, 1)',
      ],
      borderWidth: 2,
    }]
  };

  const getRiskLevelColor = (level) => {
    const colors = {
      high: 'text-red-600 bg-red-100',
      medium: 'text-yellow-600 bg-yellow-100',
      low: 'text-green-600 bg-green-100'
    };
    return colors[level] || 'text-gray-600 bg-gray-100';
  };

  const filterBySeverity = (items) => {
    if (severityFilter === 'All') return items;
    return items?.filter(item => item.severity === severityFilter.toLowerCase()) || [];
  };

  const handleExportPDF = () => {
    const doc = new jsPDF();

    // Title
    doc.setFontSize(20);
    doc.text('Document Analysis Report', 20, 20);

    // Summary
    doc.setFontSize(14);
    doc.text('Summary', 20, 40);
    doc.setFontSize(10);
    const summaryLines = doc.splitTextToSize(summary || 'No summary available', 170);
    doc.text(summaryLines, 20, 50);

    // Compliance Report
    doc.setFontSize(14);
    doc.text('Compliance Report', 20, 80);
    doc.setFontSize(10);

    const compliantData = compliance_report?.compliant_requirements?.map(req => [req, 'Compliant']) || [];
    const missingData = compliance_report?.missing_requirements?.map(req => [req, 'Missing']) || [];

    autoTable(doc, {
      head: [['Requirement', 'Status']],
      body: [...compliantData, ...missingData],
      startY: 90,
      styles: { fontSize: 8 },
      headStyles: { fillColor: [59, 130, 246] }
    });

    doc.save('document-analysis-report.pdf');
  };

  return (
    <div className="mt-8 space-y-8">
      {/* Header with Export */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-3xl font-bold text-gray-900 mb-2">Analysis Results</h2>
          <p className="text-gray-600">Comprehensive document analysis and compliance assessment</p>
        </div>
        <button
          onClick={handleExportPDF}
          className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
        >
          <ArrowDownTrayIcon className="h-5 w-5" />
          Export PDF
        </button>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Requirements"
          value={compliance_report?.total_clauses || 0}
          color="text-blue-600"
          tooltip="Total number of compliance requirements checked"
          icon={DocumentTextIcon}
        />
        <StatCard
          title="Compliant"
          value={compliance_report?.compliant_requirements?.length || 0}
          color="text-green-600"
          tooltip="Requirements that meet compliance standards"
          icon={CheckCircleIcon}
        />
        <StatCard
          title="Missing"
          value={compliance_report?.missing_requirements?.length || 0}
          color="text-red-600"
          tooltip="Requirements that need attention"
          icon={XCircleIcon}
        />
        <StatCard
          title="Risk Level"
          value={compliance_report?.risk_level || 'Unknown'}
          color="text-yellow-600"
          tooltip="Overall risk assessment of the document"
          icon={ExclamationTriangleIcon}
        />
      </div>

      {/* Summary Section */}
      {summary && (
        <div className="bg-white rounded-xl shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center gap-2">
            <DocumentTextIcon className="h-5 w-5 text-blue-500" />
            Executive Summary
          </h3>
          <p className="text-gray-700 leading-relaxed">{summary}</p>
        </div>
      )}

      {/* Tabs */}
      <div className="bg-white rounded-xl shadow-sm border border-gray-200">
        <Tab.Group selectedIndex={activeTab} onChange={setActiveTab}>
          <Tab.List className="flex space-x-1 p-4 border-b border-gray-200">
            <Tab
              className={({ selected }) =>
                classNames(
                  'flex items-center gap-2 px-4 py-2 text-sm font-medium rounded-lg transition-colors',
                  selected
                    ? 'bg-blue-100 text-blue-700'
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
                )
              }
            >
              <ChartBarIcon className="h-4 w-4" />
              Compliance Overview
            </Tab>
            <Tab
              className={({ selected }) =>
                classNames(
                  'flex items-center gap-2 px-4 py-2 text-sm font-medium rounded-lg transition-colors',
                  selected
                    ? 'bg-blue-100 text-blue-700'
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
                )
              }
            >
              <UserGroupIcon className="h-4 w-4" />
              Entities
            </Tab>
            <Tab
              className={({ selected }) =>
                classNames(
                  'flex items-center gap-2 px-4 py-2 text-sm font-medium rounded-lg transition-colors',
                  selected
                    ? 'bg-blue-100 text-blue-700'
                    : 'text-gray-500 hover:text-gray-700 hover:bg-gray-100'
                )
              }
            >
              <DocumentMagnifyingGlassIcon className="h-4 w-4" />
              Clause Traceability
            </Tab>
          </Tab.List>
          <Tab.Panels className="p-6">
            <Tab.Panel>
              <div className="space-y-6">
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  <div className="bg-gray-50 rounded-lg p-4">
                    <h4 className="text-lg font-medium text-gray-900 mb-4">Compliance Distribution</h4>
                    <div className="h-64">
                      <Doughnut 
                        data={complianceData}
                        options={{
                          responsive: true,
                          maintainAspectRatio: false,
                          plugins: {
                            legend: {
                              position: 'bottom'
                            }
                          }
                        }}
                      />
                    </div>
                  </div>
                  <div className="space-y-4">
                    <h4 className="text-lg font-medium text-gray-900">Compliance Details</h4>
                    <div className="space-y-3">
                      {compliance_report?.compliant_requirements?.map((req, idx) => (
                        <div key={idx} className="flex items-center gap-3 p-3 bg-green-50 rounded-lg border border-green-200">
                          <CheckCircleIcon className="h-5 w-5 text-green-600 flex-shrink-0" />
                          <span className="text-sm text-green-800">{req}</span>
                        </div>
                      ))}
                      {compliance_report?.missing_requirements?.map((req, idx) => (
                        <div key={idx} className="flex items-center gap-3 p-3 bg-red-50 rounded-lg border border-red-200">
                          <XCircleIcon className="h-5 w-5 text-red-600 flex-shrink-0" />
                          <span className="text-sm text-red-800">{req}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            </Tab.Panel>

            <Tab.Panel>
              <div className="space-y-6">
                <div className="flex items-center justify-between">
                  <h4 className="text-lg font-medium text-gray-900">Entity Analysis</h4>
                  <div className="flex items-center gap-2">
                    <label className="text-sm text-gray-600">Filter by severity:</label>
                    <select
                      value={severityFilter}
                      onChange={(e) => setSeverityFilter(e.target.value)}
                      className="text-sm border border-gray-300 rounded-md px-3 py-1"
                    >
                      <option value="All">All</option>
                      <option value="High">High</option>
                      <option value="Medium">Medium</option>
                      <option value="Low">Low</option>
                    </select>
                  </div>
                </div>

                {entities && entities.length > 0 ? (
                  <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                    <div className="bg-gray-50 rounded-lg p-4">
                      <h5 className="text-md font-medium text-gray-900 mb-4">Entity Distribution</h5>
                      <div className="h-64">
                        <Bar 
                          data={entityTypeData}
                          options={{
                            responsive: true,
                            maintainAspectRatio: false,
                            plugins: {
                              legend: {
                                display: false
                              }
                            },
                            scales: {
                              y: {
                                beginAtZero: true
                              }
                            }
                          }}
                        />
                      </div>
                    </div>
                    <div className="space-y-4">
                      <h5 className="text-md font-medium text-gray-900">Detected Entities</h5>
                      <div className="space-y-2 max-h-64 overflow-y-auto">
                        {filterBySeverity(entities).map((entity, idx) => (
                          <div key={idx} className="flex items-center justify-between p-3 bg-white rounded-lg border border-gray-200">
                            <div>
                              <span className="text-sm font-medium text-gray-900">{entity.text}</span>
                              <span className="text-xs text-gray-500 ml-2">({entity.label})</span>
                            </div>
                            <span className={`px-2 py-1 text-xs font-medium rounded-full ${getRiskLevelColor(entity.severity)}`}>
                              {entity.severity}
                            </span>
                          </div>
                        ))}
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <UserGroupIcon className="mx-auto h-12 w-12 text-gray-400 mb-4" />
                    <p className="text-gray-500">No entities detected in this document.</p>
                  </div>
                )}
              </div>
            </Tab.Panel>

            <Tab.Panel>
              <ClauseTraceabilitySection traceResults={clause_traceability} />
            </Tab.Panel>
          </Tab.Panels>
        </Tab.Group>
      </div>
    </div>
  );
};

export default AnalysisResults;
