import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import DocumentUpload from './components/DocumentUpload';
import AnalysisResults from './components/AnalysisResults';

function App() {
  const [analysisResults, setAnalysisResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [uploadToastId, setUploadToastId] = useState(null);

  const handleUploadComplete = (results, toastId) => {
    console.log('Analysis results:', results); // Debug log
    setAnalysisResults(results);
    setUploadToastId(toastId);
  };

  const handleError = (error) => {
    console.error('Error:', error); // Debug log
    setAnalysisResults(null);
  };

  useEffect(() => {
    if (analysisResults && uploadToastId) {
      toast.dismiss(uploadToastId);
      setUploadToastId(null);
    }
  }, [analysisResults, uploadToastId]);

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="max-w-7xl mx-auto py-6 px-4 sm:px-6 lg:px-8">
          <h1 className="text-3xl font-bold text-gray-900">DocIntel AI</h1>
        </div>
      </header>

      <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
        <div className="px-4 py-6 sm:px-0">
          <h1 className="text-3xl font-bold text-gray-900 mb-8">
            Document Intelligence
          </h1>

          <DocumentUpload
            onUploadComplete={handleUploadComplete}
            onError={handleError}
            setLoading={setLoading}
          />

          {loading && (
            <div className="mt-8 text-center">
              <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-500"></div>
              <p className="mt-2 text-gray-600">Processing document...</p>
            </div>
          )}

          {analysisResults && !loading && (
            <AnalysisResults results={analysisResults} />
          )}
        </div>
      </main>

      <ToastContainer position="bottom-right" />
    </div>
  );
}

export default App;
